    @Test
    public void testMersenneTwister_0020() {
        MersenneTwister rng = new MersenneTwister();

        rng.seed(1000000);
        long v1 = rng.nextLong();

        rng.seed(1000000);
        long v2 = rng.nextLong();

        assertEquals(v1, v2);
    }
